'use strict';
var mongoose = require('mongoose');
var connect = mongoose.connect('mongodb://localhost/clips');
var Schema = mongoose.Schema;

var Post = new Schema({
  authorId: String,
  date: { type: Date, default: Date.now },
  src: String,
  link: String,
  content: String
});

exports.Post = mongoose.model('Post', Post);

var User = new Schema({
  name: String,
  login: String,
  avatar: String,
  provider: String
});

exports.User = mongoose.model('User', User);
